package cn.edn.yan.util;public class PageParameters {	public static final int ITEMS_EACH_PAGE = 20;		public static String pageNavigator(int totalpages,int currentpage, String url){		String  strnavigate="";		strnavigate+="第"+currentpage+"页/共"+totalpages+"页&nbsp;&nbsp;";		strnavigate+="<a href='"+url+"?page=1'>|第一页</a>|";		int lastpage=currentpage-1>=1?currentpage-1:1;		strnavigate+="<a href='"+url+"?page="+lastpage+"'>上一页</a>|";		int nextpage=currentpage+1<=totalpages?currentpage+1:totalpages;		strnavigate+="<a href='"+url+"?page="+nextpage+"'>下一页</a>|";		strnavigate+="<a href='"+url+"?page="+totalpages+"'>最后一页</a>|";		return strnavigate;	}}
