package cn.edn.yan.util;public class SPageParameters {	public static final int ITEMS_EACH_PAGE = 12;		public static String pageNavigator(int totalpages,int currentpage, String url){		String  strnavigate="";		strnavigate+="第<strong>"+currentpage+"</strong>页/共<strong>"+totalpages+"</strong>页&nbsp;&nbsp;";		strnavigate+="<a href='"+url+"?spage=1'>|第一页</a>|";		int lastpage=currentpage-1>=1?currentpage-1:1;		strnavigate+="<a href='"+url+"?spage="+lastpage+"'>上一页</a>|";		int nextpage=currentpage+1<=totalpages?currentpage+1:totalpages;		strnavigate+="<a href='"+url+"?spage="+nextpage+"'>下一页</a>|";		strnavigate+="<a href='"+url+"?spage="+totalpages+"'>最后一页</a>|";		return strnavigate;	}}
